import React, { useEffect, useState } from "react";
import { db } from "../utils/firebase";
import { Link } from "react-router-dom";
import { BsTrash } from "react-icons/bs";
import { MdEdit } from "react-icons/md";
import { Modal, Button } from "react-bootstrap";
import { Container, Row, Col } from 'react-bootstrap'

export default function AllBlogs({ blog, editBlog, deleteBlog }) {
  const [blogs, setBlogs] = useState([]);
  const [id, setID] = useState();
  useEffect(async () => {
    let allBlogs = await db.collection("blogs").get();
    let oldblogs = [];
    allBlogs.docs.forEach((item) => {
      let data = { ...item.data() };
      data.id = item.id;
      oldblogs.push(data);
      oldblogs.sort((a, b) => a.createdAt - b.createdAt);
      setBlogs([...oldblogs]);
    });
  }, [blog]);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
    <div className="w-100 px-3">
      <h2>Blogs</h2>
      <div className="main-wrapper p-2">
        {blogs.length &&
          blogs.map((item, index) => {
            console.log("item", item);
            return (
              <Container>
                <Row>
                  <Col>
                    <div key={index + item.title} className="blog-wrapper">
                      <div className=" project_card creationDate justify-content-between bg-light text-dark my-2 px-2">
                        <div className="d-flex">
                        <div className='project_card_img'>
                          <img style={{ height:"200px", width: "250px", padding: "20px"}} src="https://raw.githubusercontent.com/kirankumargonti/voice-assistance-app-with-alan-ai/main/src/screenshot.jpg" alt='' />
                        </div>
                        <Row>
                          <Col>
                          <h5 className="font-weight-bold pt-5"
                            style={{ alignItems: "center" }}>
                            <Link to={`/blog/${item.id}`} className="ml-md-2">
                              Title: <span>{item.title}</span>
                            </Link>
                          </h5>
                          </Col>
                          </Row>
                        </div>
                        <Row>
                          <Col>
                            <h5 className="ont-weight-bold pt-5"
                              style={{ alignItems: "center" }}>
                              <span className="ml-md-2">
                                Posting Time: {new Date(item.createdAt)
                                  .toLocaleString("en-GB", { timeZone: "UTC" })
                                  .toString()}
                              </span>
                            </h5>
                          </Col>
                        </Row>
                  <div
                    className="creditsAndActions"
                    style={{ alignItems: "center" }}
                  >
                    <p className=" mb-0">By: {item.createdBy}</p>
                    <div
                      className="btn btn-sm rounded-circle"
                      onClick={() => {
                        handleShow();
                        setID(item.id);
                      }}
                    >
                      <BsTrash></BsTrash>
                    </div>

                    <div
                      className="btn btn-sm rounded-circle"
                      onClick={() => {
                        editBlog(blogs.filter((i) => i.id === item.id)[0]);
                      }}
                    >
                      <MdEdit></MdEdit>
                    </div>
                    <Modal show={show} onHide={handleClose}>
                      <Modal.Header closeButton>
                        <Modal.Title>Deleting BLog Document</Modal.Title>
                      </Modal.Header>
                      <Modal.Body>
                        Are you sure to <strong>Delete</strong> this record from
                        Database?
                      </Modal.Body>
                      <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                          Close
                        </Button>
                        <Button
                          variant="danger"
                          onClick={() => {
                            deleteBlog(id);
                          }}
                        >
                          Save Changes
                        </Button>
                      </Modal.Footer>
                    </Modal>
                  </div>
                </div>
              </div>
                  </Col>
                </Row>
              </Container>
            );
          })}
      </div>
    </div>
  );
}
